import React, { Component } from "react";

import UserService from "../services/user.service";
import EventBus from "../common/EventBus";

export default class BoardCompany extends Component {
  constructor(props) {
    super(props);

    this.state = {
      content: ""
    };
  }

  componentDidMount() {
    UserService.getCompanyBoard().then(
      response => {
        this.setState({
          content: response.data
        });
      },
      error => {
        this.setState({
          content:
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString()
        });

        if (error.response && error.response.status === 401) {
          EventBus.dispatch("logout");
        }
      }
    );
  }

  render() {
    return (
      <div className="container">
        
        <div>
          <Routes>
            <Route exact path="/" element={<CouponList />} />
            <Route path="/addcoupons" element={<AddCoupons/>} />
            <Route path="/coupons/edit/:com_id" element={<AddCoupons />} />
            <Route path="/*" element={<NotFound />} />
          </Routes>
        </div>
      
      </div>
    );
  }
}
