import React, { Component } from 'react'
import CompanyService from '../services/CompanyService';

class createcoupons extends Component {
    constructor(props) {
        super(props)

        this.state = {
            // step 2
            company_id: this.props.match.params.company_id,
            comName: '',
            location: '',
            product_category: '',
            coupon:{
            coupon_id: '',
            coupon_code:'',
            coupon_set_name:'',
            discount_amount:'',
            product_id: '',
            product_name:'',
            price: '',
            category: ''
            }


               
        }
        this.changecomNameHandler = this.changecomNameHandler.bind(this);
        this.changeLocationHandler = this.changeLocationHandler.bind(this);
        this.changecouponHandler=this.changecouponHandler.bind(this);
        this.saveOrUpdateCoupons = this.saveOrUpdateCoupons.bind(this);
    }
    componentDidMount()
    {

      // step 4
      if(this.state.company_id === '_add'){
          return
      }else{
          CompanyService.getCouponByCompanyId(this.state.company_id).then( (res) =>{
              let company = res.data;
              this.setState({comName: this.state.comName, 
                location: this.state.location, 
                product_category: this.state.product_category,
                coupon:{ coupon_id:this.state.coupon_id,
                  coupon_code:this.state.coupon_code,
                  coupon_set_name:this.state.coupon_set_name,
                  discount_amount:this.state.discount_amount,
                  product_id:this.state.product_id,
                  product_name:this.state.product_name,
                  price:this.state.price,
                
                }
              });
          });
      }        
  }
   
    saveOrUpdateCoupons= (e) =>
     {
        e.preventDefault();
        let company = {comName: this.state.comName, 
                      location: this.state.location, 
                      product_category: this.state.product_category,
                      coupon:{
                        coupon_id:this.state.coupon_id,
                        coupon_code:this.state.coupon_code,
                        coupon_set_name:this.state.coupon_set_name,
                        discount_amount:this.state.discount_amount,
                        product_id:this.state.product_id,
                        product_name:this.state.product_name,
                        price:this.state.price,
                        
                      }

                      };
                     
        console.log('company=> ' + JSON.stringify(company));
        
        // step 5
        if(this.state.id === '_add'){
          CompanyService.createcoupons(company).then(res =>{
              this.props.history.push('/coupons');
          });
      }else{
          CompanyService.updateCouponById(company, this.state.company_id).then( res => {
              this.props.history.push('/coupons');
          });
      }
  }
  changecomNameHandler= (event) => {
        this.setState({comName: event.target.value});
    }
  changeLocationHandler=(event)=>{
      this.setState({location:event.target.value});
    }
  changeproductcategoryHandler=(event)=>{
      this.setState({product_category:event.target.value});
    }
    changecouponHandler=(event)=>{
      this.setState({coupon_id:event.target.value});
      this.setState({coupon_code:event.target.value});
      this.setState({coupon_set_name:event.target.value});
      this.setState({discount_amount:event.target.value});
      this.setState({product_id:event.target.value});
      this.setState({product_name:event.target.value});
      this.setState({price:event.target.value});
      
    }
    

   

    cancel(){
        this.props.history.push('/coupons');
    }

    getTitle(){
        if(this.state.id === '_add'){
            return <h3 className="text-center">Add Coupons</h3>
        }else{
            return <h3 className="text-center">Update Coupons</h3>
        }
    }
    render() {
        return (
            <div>
                <br></br>
                   <div className = "container">
                        <div className = "row">
                            <div className = "card col-md-6 offset-md-3 offset-md-3">
                                {
                                    this.getTitle()
                                }
                                <div className = "card-body">
                                    <form>
                                        <div className = "form-group">
                                            <label> comName: </label>
                                            <input placeholder="Company Name" name="CompanyName" className="form-control" 
                                                value={this.state.comName} onChange={this.changecomNameHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label>location:</label>
                                            <input placeholder="Location" name="location" className="form-control" 
                                                value={this.state.location} onChange={this.changeLocationHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> product_category: </label>
                                            <input placeholder="Product category" name="product_category" className="form-control" 
                                                value={this.state.product_category} onChange={this.changeproductcategoryHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> coupon_id: </label>
                                            <input placeholder="Coupon_id" name="coupon_id" className="form-control" 
                                                value={this.state.coupon_id} onChange={this.changecouponHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> coupon_code: </label>
                                            <input placeholder="Coupon_code" name="coupon_code" className="form-control" 
                                                value={this.state.coupon_code} onChange={this.changecouponHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> coupon_set_name: </label>
                                            <input placeholder="coupon_set_name" name="coupon_set_name" className="form-control" 
                                                value={this.state.coupon_set_name} onChange={this.changecouponHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> discount_amount: </label>
                                            <input placeholder="discount_amount" name="discount_amount" className="form-control" 
                                                value={this.state.discount_amount} onChange={this.changecouponHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> product_id: </label>
                                            <input placeholder="product_id" name="product_id" className="form-control" 
                                                value={this.state.product_id} onChange={this.changecouponHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> product_name: </label>
                                            <input placeholder=" product_name" name=" product_name" className="form-control" 
                                                value={this.state. product_name} onChange={this.changecouponHandler}/>
                                        </div>
                                        <div className = "form-group">
                                            <label> price: </label>
                                            <input placeholder="price" name="price" className="form-control" 
                                                value={this.state.price} onChange={this.changecouponHandler}/>
                                        </div>
                                        

                                        <button className="btn btn-success" onClick={this.saveOrUpdateCoupons}>Save</button>
                                        <button className="btn btn-danger" onClick={this.cancel.bind(this)} style={{marginLeft: "10px"}}>Cancel</button>
                                    </form>
                                </div>
                            </div>
                        </div>

                   </div>
            </div>
        )
    }
}

export default createcoupons
