import httpClient from "../http-common2";

const getAll = () => {
  return httpClient.get("/coupons");
};

const get = (com_id) => {
  return httpClient.get(`/couponsbyId/${com_id}`);
};

const create = (data) => {
  return httpClient.post("/savecoupons", data);
};

const update = (data) => {
  return httpClient.put("/updatecoupons", data);
};

const remove = (com_id) => {
  return httpClient.delete("/deletecoupons/${com_id}");
};

export default { getAll, create, get, update, remove };
