import React from "react";

const ContactForm = ({ values, setValues, onSubmit }) => (
  <div className="App">
    <p>Contact Us</p>
    <div>
      <form
        onSubmit={evt => {
          evt.preventDefault();
          onSubmit();
        }}
      >
        <label>Name</label>
        <input
          type="text"
          id="fname"
          name="firstname"
          placeholder="Your name.."
          value={values.name}
          onChange={evt => setValues({ ...values, name: evt.target.value })}
        />
        {/*      <label>Last Name</label>
        <input
          type="text"
          id="lname"
          name="lastname"
          placeholder="Your last name.."
        /> */}

        <label>Email</label>
        <input
          type="email"
          id="email"
          name="email"
          placeholder="Your email"
          value={values.email}
          onChange={evt => setValues({ ...values, email: evt.target.value })}
        />

        <label>Message</label>
        <textarea
          value={values.message}
          placeholder="Type you message here.."
          onChange={evt => setValues({ ...values, message: evt.target.value })}
        />
        <button type="submit">SUBMIT</button>
      </form>
    </div>
  </div>
);

export default ContactForm;
