import React from "react";
import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import CompanyService from "../services/CompanyService";

const AddCoupons = () => {
  const [comName, setComName] = useState("");
  const [location, setLocation] = useState("");
  const [product_category, setProduct_Category] = useState("");
  const [coupon, setCoupon] = useState("");
  const [coupon_code, setCoupon_code] = useState("");
  const [discount_amount, setDiscount_amount] = useState("");
  const [product_id, setProduct_id] = useState("");
  const [product_name, setProduct_name] = useState("");
  const [price, setPrice] = useState("");
  
  const navigate = useNavigate();
  const { com_id } = useParams();

  const saveCoupons= (o) => {
    o.preventDefault();
    const company= { com_id,comName,location,product_category};
    const coupon = { coupon_code, discount_amount, product_id, product_name,price};

    if (com_id) {
      //update
      CompanyService.update(company)
        .then((response) => {
          alert("Coupon Data Updated successfully!");
          console.log("Coupon data updated successfully", response.data);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    } else {
      // create
      CompanyService.create(company)
        .then((response) => {
          alert("Coupon Saved");
          console.log("Coupon has been added successfully", response.data);
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  };

  useEffect(() => {
    if (com_id) {
      CompanyService.get(com_id)
        .then((company) => {
          setComName(company.data.comName);
          setLocation(company.data.location);
          setProduct_Category(coupon.data.product_category);
          setCoupon(setCoupon_code(coupon.data.coupon_code),
                    setDiscount_amount(coupon.data.discount_amount),
                   setProduct_id(coupon.data.product_id),
                  setProduct_name(coupon.data.product_name),
                  setPrice(coupon.data.price)
          );
        })
        .catch((error) => {
          console.log("Something went wrong", error);
        });
    }
  }, []);

  return (
    <div className="container">
      <br></br>
      <br></br>

      <br></br>
      <h3 className="ab">
        <b>
          <u>Add/Update Coupons</u>
        </b>
      </h3>
      <form className="form1">
        <br></br>
        <br></br>
        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Name:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="comname"
            value={comName}
            onChange={(o) => setComName(o.target.value)}
            placeholder="Enter Company Name"
          />
        </div>

        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Price:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="location"
            value={location}
            onChange={(o) =>setLocation(o.target.value)}
            placeholder="Enter location"
          />
        </div>
        <br></br>
        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Price:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="productcate"
            value={product_category}
            onChange={(o) =>setProduct_Category(o.target.value)}
            placeholder="Enter product category"
          />
        </div>
        <br></br>
        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Price:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="coucode"
            value={coupon}
            onChange={(o) =>setCoupon_code(o.target.value)}
            placeholder="Enter Coupon Code"
          />
        </div>
        <br></br>
        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Price:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="discountmnt"
            value={discount_amount}
            onChange={(o) =>setDiscount_amount(o.target.value)}
            placeholder="Enter Discount Amount"
          />
        </div>
        <br></br>
        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Price:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="productid"
            value={product_id}
            onChange={(o) =>setProduct_id(o.target.value)}
            placeholder="Enter product ID"
          />
        </div>
        <br></br>
        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Price:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="productname"
            value={product_name}
            onChange={(o) =>setProduct_name(o.target.value)}
            placeholder="Enter product name"
          />
        </div>
        <br></br>
        <br></br>
        <div className="form-group">
          {/* <p className="prsub">Product Price:</p> */}
          <input
            type="text"
            className="form-control col-4"
            id="price"
            value={price}
            onChange={(o) =>setPrice(o.target.value)}
            placeholder="Enter price"
          />
        </div>
        <br></br>
        <br></br>
        <div>
          <button onClick={(o) => saveCoupons(o)} id="svep">
            Save
          </button>
          <Link to="/couponslist">
            <button className="bckc">Back To list</button>
          </Link>
        </div>
      </form>
    </div>
  );
};

export default AddCoupons;
