import React from "react";
import { useEffect, useState } from "react";
import "./style.css";
import { Link } from "react-router-dom";
import CompanyService from "../services/CompanyService";

const CouponsList = () => {
  const [company, setCompany] = useState([]);

  const init = () => {
    CompanyService.getAll()
      .then((response) => {
        console.log("Printing Products", response.data);
        setCompany(response.data);
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };

  useEffect(() => {
    init();
  }, []);

  const handleDelete = (company_id) => {
    alert("Are You Sure ?")
    console.log("Printing id", company_id);
    CompanyService.remove(company_id)
      .then((response) => {
        console.log("Product has been Deleted", response.data);
        init();
      })
      .catch((error) => {
        console.log("Something went wrong", error);
      });
  };

  return (
    <div className="container" id="plist">
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <h3>List of Products</h3>
      <hr />
      <div>
        <Link to="/addcoupons" className="btn  mb-2" id="aprod">
          Add Coupons
        </Link>
        <table className="table table-bordered table-striped" id="tabp">
          <thead className="thead-dark">
          <tr>
          <td>Company Name</td>
          <td>location</td>
          <td>Product Category</td>
          <td>Coupon Code</td>
          <td>Coupon Discount</td>
          <td>Product ID</td>
          <td>Product Name</td>
          <td>Product Price</td>
          <td>Actions</td>
          </tr>
          </thead>
          <tbody>
            {company.map((company) => (
              <tr key={company.company_id}>
                <td>{company.location}</td>
                <td>{company.product_category}</td>
                <td>{company.coupon_code}</td>
                <td>{company.discount_amount}</td>
                 <td>{company.product_id}</td>
                 <td>{company.product_name}</td>
                 <td>{company.price}</td>
                <td>
                  <Link
                    className="btn "
                    id="uprod"
                    to={`/coupons/edit/${company.company_id}`}
                  >
                    Edit 
                  </Link>

                  <button
                    id="dprod"
                    onClick={() => {
                      handleDelete(company.company_id);
                    }}
                  >
                    Delete 
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CouponsList;
