import axios from 'axios';

const COMPANY_API_BASE_URL = "http://localhost:8008/cnd/coupons";

class CompanyService {

    gettotalcoupons(){
        return axios.get(COMPANY_API_BASE_URL);
    }

    createcoupons(com){
        return axios.post(COMPANY_API_BASE_URL,com);
    }
    updateCouponById(company_id){
        return axios.put(COMPANY_API_BASE_URL+ '/' + company_id);
    }

    deleteProductsById(company_id){
        return axios.delete(COMPANY_API_BASE_URL+ '/' + company_id);
    }
    getCouponByCompanyName(comName){
        return axios.get(COMPANY_API_BASE_URL + '/' + "couponsbyCompany"+'/'+comName);
    }
    getCouponByProductCategory(product_category){
        return axios.get(COMPANY_API_BASE_URL+ '/' + "couponsbyCategory"+'/'+product_category)
    }
    getCouponByCompanyId(coupon_id){
        return axios.get(COMPANY_API_BASE_URL+ '/' + "couponsbyId"+'/'+ coupon_id);
    }
}

export default new CompanyService()
